select concat ('The film "', f_n.name_of_film, '" was made by ' , d.name, ' ' , d.surname, ' in ', year,'. Stars:', a1.name, ' ' , a1.surname_first, ' ,',a2.name, ' ' , a2.surname_first,' ,',a3.name, ' ' , a3.surname_first, ' ,',a4.name, ' ' , a4.surname_first, ' ,',a5.name, ' ' , a5.surname_first, ' ,',a6.name, ' ' , a6.surname_first, '.') Preview from films f inner join film_name  f_n on (f.name_of_film = f_n.id) inner join directors  d on (f.director = d.id) inner join casting c on (f.cast_film = c.id_film) inner join actors a1 on (c.actor_1= a1.id) inner join actors a2 on (c.actor_2= a2.id) inner join actors a3 on (c.actor_3= a3.id) inner join actors a4 on (c.actor_4= a4.id) inner join actors a5 on (c.actor_5= a5.id) inner join actors a6 on (c.actor_6= a6.id);